sys_script.script:
function inactivateFlag(flag) {
    var scheduler = new GlideRecord('sysauto_script');
    var sysID = scheduler.addQuery('sys_id', gs.getProperty("x_inmis_im_itad.Pull Box Label Order- Release for Ship, Picking and Shipped Data from Blue IQ")); //Pull Box Label Order- Release for Ship, Picking and Shipped Data from Blue IQ
	sysID.addOrCondition('sys_id', gs.getProperty("x_inmis_im_itad.Pull Box Label Order- Asset Level and Invoiced Data from Blue IQ")); //Pull Box Label Order- Asset Level and Invoiced Data from Blue IQ
    scheduler.query();
    while (scheduler.next()) {
        scheduler.active = flag;
        scheduler.autoSysFields(false);
        scheduler.setWorkflow(false);
        scheduler.update();
    }

}

function inactivateInboundAPI(flag) {
    var api = new GlideRecord('sys_ws_operation');
    api.addQuery('web_service_definition', gs.getProperty("x_inmis_im_itad.IMITAD Inbound Box Label Order scripted rest")); //IMITAD Inbound Box Label Order
    api.query();
    while (api.next()) {
        api.active = flag;
        api.setWorkflow(false);
        api.autoSysFields(false);
        api.update();
    }
}


(function executeRule(current, previous /*null when async*/ ) {
    try {
        var SchedulerBoxLabelOrder = current.getValue('value');

        if (SchedulerBoxLabelOrder.toString().toLowerCase() == 'yes') {
            inactivateFlag(true);
            inactivateInboundAPI(false);
        } else if (SchedulerBoxLabelOrder.toString().toLowerCase() == 'no') {
            inactivateFlag(false);
            inactivateInboundAPI(true);
        }
    } catch (e) {
        gs.info('>>>>Exception occured [Update schedule job based on property]: ' + e);
    }
})(current, previous);
