  //  var query = 'sys_id=' + gs.getProperty('x_inmis_im_itad.Pull Box Label Master Data E/D');
    var scheduler = new GlideRecord('sysauto_script');
	scheduler.get(gs.getProperty('x_inmis_im_itad.ScheduleScriptId'));
   // scheduler.addEncodedQuery('sys_id=07c7e588dbf6d4108a521fe9689619ee'); // Pull Box Label Master Data
   // scheduler.query();
  //  if (scheduler.next()) {
        scheduler.active = flag;
        scheduler.autoSysFields(false);
        scheduler.setWorkflow(false);
        scheduler.update();
   // }
}

function inactiveApplicationMenu(flag) {
    var ids = gs.getProperty('x_inmis_im_itad.BoxMenuItemsid').split(',');
    var query = '';
    for (var i = 0; i < ids.length; i++) {
        if (i == 0) {
            query = 'sys_id=' + ids[i];
        } else {
            query += '^ORsys_id=' + ids[i];
        }
    }
    if (query != '') {
        var appMenu = new GlideRecord('sys_app_module');
        // appMenu.addEncodedQuery('sys_id=4a062cf8db7258108a521fe9689619a4^ORsys_id=595de728dbfe18108a521fe968961958^ORsys_id=a70c28e6db47501067166ac2ca961978^ORsys_id=94b93ac3db4f901067166ac2ca9619af');
        appMenu.addEncodedQuery(query);
        appMenu.query();
        while (appMenu.next()) {
            appMenu.active = flag;
            appMenu.autoSysFields(false);
            appMenu.setWorkflow(false);
            appMenu.update();
        }

    }
}
(function executeRule(current, previous /*null when async*/ ) {
    try {
        var BoxLabelValue = current.getValue('value');

        if (BoxLabelValue.toString().toLowerCase() == 'yes') {
            inactivateFlag(true);
            inactiveApplicationMenu(true);
        } else if (BoxLabelValue.toString().toLowerCase() == 'no') {
            inactivateFlag(false);
            inactiveApplicationMenu(false);
        }
    } catch (e) {
        gs.info('>>>>Exception occured [Enable/Disable the schedule job]: ' + e);
    }

})(current, previous);
