sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    try {  

        gs.info("WorkOrder Number: " + current.number + " Before Business rule started.");
        var assetlist = current.u_glide_list_1.toString();
        var array = assetlist.split(","); 

        //Validate Serial Number and Equipment type Mapping
        for (var assetInfo = 0; assetInfo < array.length; assetInfo++) {
            var record = new GlideRecord('alm_asset');
            if (record.get(array[assetInfo])) {
				// commented as part of US6387
//                 if(record.serial_number.nil()) {
// 					current.workorder_status = 'draft';
//                     current.setAbortAction(true);
//                     gs.addErrorMessage('Serial Number not exist');
//                     gs.info('WorkOrder -aborted serialnumbernotexist-------');
//                     return;
//                 }

                var EquipmentMap = new GlideRecord('x_inmis_im_itad_im_mapping_model_equipment');
                EquipmentMap.addQuery('mapping_model_categories', record.getValue('model_category'));
                EquipmentMap.query();
                if (EquipmentMap.next()) {
                    if (EquipmentMap.mapping_equipment_type.equipmenttypeid == '') {
						current.workorder_status = 'draft';
                        current.setAbortAction(true);
                        gs.addErrorMessage('Workorder cannot be submitted, as there is model category ( ' + record.getDisplayValue('model_category') + ' ) of an asset not mapped to equipment type. Please contact ITAD administrator');
                        return;
                    }

                } else { 
					current.workorder_status = 'draft';
                    current.setAbortAction(true);
                    gs.addErrorMessage('Workorder cannot be submitted, as there is model category ( ' + record.getDisplayValue('model_category') + ' ) of an asset not mapped to equipment type. Please contact ITAD administrator');
                    return;
                }

            }
        }
        gs.info('WorkOrder :' + current.number + ':Before Business rule completed.');
    } catch (ex) {
        var message = ex.message;
        gs.info('WorkOrder Number:' + current.number + ':Error occured:' + message);
        gs.error(ex);
        current.setAbortAction(true);
    }		



})(current, previous);
