sys_script_client.script:
function clearShippingLocationValue() {
    g_form.setValue('shipping_address', '');
    g_form.setValue('shipping_address2', '');
    g_form.setValue('shipping_address3', '');
    g_form.setValue('shipping_city', '');
    g_form.setValue('shipping_state', '');
    g_form.setValue('shipping_postal_code', '');
    g_form.setValue('shipping_country', '');
}

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {

        return;
    }
  if (newValue === '') {
    clearShippingLocationValue();
}
    var ga = new GlideAjax('BoxLabelUtils');
    ga.addParam('sysparm_name', 'getLocation');
    ga.addParam('sysparm_value', newValue);
    ga.getXML(getLocationDetails);


}


function getLocationDetails(response) {
    var obj1 = response.responseXML.documentElement.getAttribute('answer');
    var userObj = JSON.parse(obj1);
    g_form.setValue('shipping_address', userObj.street);
    g_form.setValue('shipping_address2', userObj.street2);
    g_form.setValue('shipping_address3', userObj.street3);
    g_form.setValue('shipping_city', userObj.city);
    g_form.setValue('shipping_state', userObj.state);
    g_form.setValue('shipping_postal_code', userObj.postalCode);
    g_form.setValue('shipping_country', userObj.country);

//     var country = g_form.getValue('shipping_country');
