sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
//         if (newValue === '') {
//             g_form.setValue('return_address', '');
//             g_form.setValue('return_address2', '');
//             g_form.setValue('return_address3', '');
//             g_form.setValue('return_city', '');
//             g_form.setValue('return_state', '');
//             g_form.setValue('return_postal_code', '');
//             g_form.setValue('return_country', '');
//         }
        return;
    }

    var ga = new GlideAjax('BoxLabelUtils');
    ga.addParam('sysparm_name', 'getLocation');
    ga.addParam('sysparm_value', newValue);
    ga.getXML(getLocationDetails);

    function getLocationDetails(response) {
        var obj1 = response.responseXML.documentElement.getAttribute('answer');
        var userObj = JSON.parse(obj1);
        g_form.setValue('return_address', userObj.street);
        g_form.setValue('return_address2', userObj.street2);
        g_form.setValue('return_address3', userObj.street3);
        g_form.setValue('return_city', userObj.city);
        g_form.setValue('return_state', userObj.state);
        g_form.setValue('return_postal_code', userObj.postalCode);
        g_form.setValue('return_country', userObj.country);
    }
}
