sys_script_client.script:
function clearReturnAddress() {
    g_form.setValue('return_address', '');
    g_form.setValue('return_address2', '');
    g_form.setValue('return_address3', '');
    g_form.setValue('return_city', '');
    g_form.setValue('return_state', '');
    g_form.setValue('return_postal_code', '');
    g_form.setValue('return_country', '');
}

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue && g_form.getValue('return_to') && g_form.getValue('shipping_state')) {
        var NCBLogic = new GlideAjax('BoxLabelUtils');
        NCBLogic.addParam('sysparm_name', 'getNCBLocationDetails');
        NCBLogic.addParam('sysparm_state', g_form.getValue('shipping_state'));
        NCBLogic.addParam('sysparm_shippingCountry', newValue);
        NCBLogic.addParam('sysparm_returnTo', g_form.getValue('return_to'));
        NCBLogic.getXML(NCBLocationDetails);
    }

    function NCBLocationDetails(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute("answer");
            //alert('answer: ' + answer);
            var array = JSON.parse(answer);
            var returnAddressFlag = array.returnAddressFlag;

            if (returnAddressFlag.toString() == 'true') {
                g_form.setValue('return_address', array.returnToAddress1);
                g_form.setValue('return_address2', array.returnToAddress2);
                g_form.setValue('return_address3', array.returnToAddress3);
                g_form.setValue('return_city', array.returnToCity);
                g_form.setValue('return_state', array.returnToState);
                g_form.setValue('return_postal_code', array.returnToPotalCode);
                g_form.setValue('return_country', array.returnToCountry);
                g_form.setValue('return_contact', array.returnContactName ? array.returnContactName : '');
                g_form.setValue('return_contact_email', array.returnContactEmail ? array.returnContactEmail : '');
                g_form.setValue('return_contact_phone', array.returnContactPhone ? array.returnContactPhone : '');
            } else {
                if (array.errorMessage) {
                    alert(array.errorMessage);
                    g_form.clearValue('shipping_country');
                }
                //    clearReturnAddress();
            }
        } catch (e) {
            alert('>>>Exception occured: ' + e);
        }

    }
}
