sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var email = newValue.trim();
    var isValidEmail = validateEmail(email);

    if (!isValidEmail) {
        g_form.setValue('requestor_email', '');
        g_form.showErrorBox('requestor_email', "Please enter a valid email address. You entered: " + email);
    }
}

function validateEmail(email) {
    if (!email || email.trim().length <= 0)
        return true;
    var re = /^[^@]+@[^@]+.[a-z]{2,}$/i;
    if (!re.test(email)) {
        return false;
    }
    return true;
}
