sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    try {
        if (isLoading || newValue === '') {
            return;
        }
        var returnFlags = new GlideAjax('BoxLabelUtils');
        returnFlags.addParam('sysparm_name', 'checkOrderType');
        returnFlags.addParam('sysparm_returnTo', newValue);
        returnFlags.getXML(checkOrderTypeValue);

    } catch (e) {
        //alert('>>>Exception occured: ' + e);
    }
}


function checkOrderTypeValue(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");

    if (answer == true || answer.toString() == 'true') {
        //Initialize and open the Dialog Window
        var dialog = new GlideDialogWindow("x_inmis_im_itad_Box_Reclaim_Validation"); //Render the dialog containing the UI Page 'task_comments_dialog'
        dialog.setTitle("Please enter the Quantity of Parts"); //Set the dialog title
        dialog.setSize(700, 200);
        dialog.render(); //Open the dialog
    }

}
