sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var IMITADHadware = new GlideRecord('x_inmis_im_itad_imitad_hardware');
    if (IMITADHadware.get(current.getValue('document_id'))) {

        template.print('Hardware Asset: ' + IMITADHadware.getDisplayValue('hardware_assets') +'\n');
        template.print('Agent Id: ' + IMITADHadware.getDisplayValue('agent_id') +'\n');
        template.print('Erasure Initiated Date: ' + IMITADHadware.getDisplayValue('erasure_initiated_date') +'\n');
        template.print('Erasure Expected Date: ' + IMITADHadware.getDisplayValue('erasure_expected_date') +'\n');
        template.print('Erasure Status: ' + IMITADHadware.getDisplayValue('erasure_status'));
    }

})(current, template, email, email_action, event);
