sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var str = 'The erasure request initiated for the hardware asset ' + current.hardware_assets.getDisplayValue() + ' and Serial# ' + current.agent_id + ' on ' + current.erasure_initiated_date;

    var str1 = '';
    var approver = new GlideRecord('sysapproval_approver');
    approver.addEncodedQuery('document_id=' + current.getValue('sys_id') + '^state=rejected');
    approver.query();
    if (approver.next()) {
		var comments = approver.comments.getJournalEntry(1).toString().split('(Comments)');

        str1 = ' has been rejected by ' + approver.approver.getDisplayValue() + ' with comments ' + comments[1];
    }

    str += str1;

    template.print('<p><font size="2" face="arial">' + str +'</p>');



})(current, template, email, email_action, event);
