sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var sum = JSON.parse(event.parm2);
    if (sum.successArr.length > 0) {
        template.print("<b>Below Locations have been successfully Created/Updated</b><br>");
        for (var i = 0; i < sum.successArr.length; i++) {
            template.print(sum.successArr[i] + "<br>");
        }
    }
    if (sum.failedArr.length > 0) {
        template.print("<br><b>Below Locations have been failed to Create/Update</b><br>");
        for (var j = 0; j < sum.failedArr.length; j++) {
            template.print(sum.failedArr[j] + "<br>");
        }
    }

})(current, template, email, email_action, event);
