sys_script_fix.script:
function getWorkOrderTypeValue(workOrder) {
    try {
        var encodedQuery = 'number=' + workOrder.getValue('workorder').toString().trim();
        var wo = new GlideRecord('x_inmis_im_itad_workorder');
        wo.addEncodedQuery(encodedQuery);
        wo.query();
        if (wo.next()) {
            return wo.getValue('u_wotype') == '5123' ? 'Pickup' : 'Delivery';
        } else {
            return '';
        }
    } catch (e) {
        gs.info('>>Exception occured: ' + e);
    }
}

function updateOrderType() {
    var workOrder = new GlideRecord('x_inmis_im_itad_imitad_hardware');
    workOrder.addEncodedQuery('workorderSTARTSWITHWO');
    //    workOrder.setLimit(3);
    workOrder.query();
    while (workOrder.next()) {
        gs.info('Workoder : ' + workOrder.workorder);
        workOrder.order_type = getWorkOrderTypeValue(workOrder);
        workOrder.setWorkflow(false);
        workOrder.autoSysFields(false);
        workOrder.update();
    }
}



updateOrderType();
