function updateInboundInstanceName() {
    try {
        var instance = 'https://' + gs.getProperty('instance_name');
        var property = new GlideRecord('sys_properties');
        property.addEncodedQuery('nameINx_inmis_im_itad.BaseURI,x_inmis_im_itad.ScheduledURI,x_inmis_im_itad.InvoicedURI,x_inmis_im_itad.ReceivedURI,x_inmis_im_itad.AssetLevelAttachmentURI,x_inmis_im_itad.AttachmentURI,x_inmis_im_itad.BoxLabelOrderUpdateURI,x_inmis_im_itad.BoxLabelOrderAssetLevelURI,x_inmis_im_itad.ErasureRegisterURI,x_inmis_im_itad.ErasureStatusUpdateURI');
        property.query();
        while (property.next()) {
            switch (property.name.toString()) {
                case 'x_inmis_im_itad.BaseURI':
                    var baseURI = instance + '.service-now.com';
                    property.value = baseURI;
                    break;
                case 'x_inmis_im_itad.ScheduledURI':
                    var ScheduledURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound/Scheduled';
                    property.value = ScheduledURI;
                    break;
                case 'x_inmis_im_itad.ReceivedURI':
                    var ReceivedURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound/Received';
                    property.value = ReceivedURI;
                    break;
                case 'x_inmis_im_itad.InvoicedURI':
                    var InvoicedURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound/Invoiced';
                    property.value = InvoicedURI;
                    break;
                case 'x_inmis_im_itad.AssetLevelAttachmentURI':
                    var AssetLevelAttachmentURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound/AssetLevelAttachment';
                    property.value = AssetLevelAttachmentURI;
                    break;
                case 'x_inmis_im_itad.AttachmentURI':
                    var AttachmentURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound/Attachment';
                    property.value = AttachmentURI;
                    break;
                case 'x_inmis_im_itad.BoxLabelOrderUpdateURI':
                    var BoxLabelOrderUpdateURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound_box_label_order/box_label_order_update';
                    property.value = BoxLabelOrderUpdateURI;
                    break;
                case 'x_inmis_im_itad.BoxLabelOrderAssetLevelURI':
                    var BoxLabelOrderAssetLevelURI = instance + '.service-now.com/api/x_inmis_im_itad/imitad_inbound_box_label_order/box_label_order_asset_level';
                    property.value = BoxLabelOrderAssetLevelURI;
                    break;
					case 'x_inmis_im_itad.ErasureRegisterURI':
                    var ErasureRegisterURI = instance + '.service-now.com/api/x_inmis_im_itad/blancco_service/Register';
                    property.value = ErasureRegisterURI;
                    break;
                case 'x_inmis_im_itad.ErasureStatusUpdateURI':
                    var ErasureStatusUpdateURI = instance + '.service-now.com/api/x_inmis_im_itad/blancco_service/StatusUpdate';
                    property.value = ErasureStatusUpdateURI;
                    break;


            }
            property.update();

        }


    } catch (e) {
        gs.info('>>Exception occured [Instance Property URI Update] ' + e);
    }

}


function updateOutboundAPI() {
    try {
        var property = new GlideRecord('sys_properties');
        property.addEncodedQuery('nameINx_inmis_im_itad.TestConnection,x_inmis_im_itad.CreateLocation,x_inmis_im_itad.CreateJob,x_inmis_im_itad.CustomerRegistration URI,x_inmis_im_itad.Scheduled,x_inmis_im_itad.Received,x_inmis_im_itad.Invoiced,x_inmis_im_itad.Attachment,x_inmis_im_itad.AssetLevelAttachment,x_inmis_im_itad.UpdateApiStatus,x_inmis_im_itad.BoxLabelOrderMasterData,x_inmis_im_itad.CreateBoxLabelOrder,x_inmis_im_itad.OrderStatus,x_inmis_im_itad.AssetStatus');
        property.query();
        while (property.next()) {
            switch (property.name.toString()) {
                case 'x_inmis_im_itad.TestConnection':
