sys_script_include.script:
var IMITADLocationFilterDetailUtils = Class.create();
IMITADLocationFilterDetailUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	locationReferenceQualifier : function()
	{
		var qString = "";
		var grLocation = new GlideRecord('x_inmis_im_itad_filter');
		grLocation.addQuery('table','cmn_location');
		grLocation.query();
		while(grLocation.next())
		{
				qString = "^"+grLocation.u_filter;
		}

		return qString;
	},

	locationModuleFilter : function()
	{
		var Data = "";
		var qString = "";
		var sysIDs = [];

		var grproperty = new GlideRecord('x_inmis_im_itad_filter');
		grproperty.addQuery('table','cmn_location');
		grproperty.query();
		while(grproperty.next())
		{
				qString = "^"+grproperty.u_filter;
		}

		if (qString != '')
		{
				var grLocation = new GlideRecord('cmn_location');
				grLocation.addEncodedQuery(qString);
				grLocation.query();
				while(grLocation.next())
				{
					sysIDs.push(grLocation.getValue('sys_id'));
				}
			return sysIDs;
		}else
			{
				var grL = new GlideRecord('cmn_location');
				grL.query();
				while(grL.next())
				{
					sysIDs.push(grL.getValue('sys_id'));
				}
			return sysIDs;
			}
	},

    type: 'IMITADLocationFilterDetailUtils'
});
