sys_script_include.script:
var companyUtil = Class.create();
companyUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCompanyName: function() {
        var compName = ''; // Return value
        var grComp = new GlideRecord('core_company');
        if (grComp.get(gs.getUser().getCompanyID())) {
            compName = grComp.sys_id;
        }
        return compName;
    },
    requestor_info: function() {
        var result = this.newItem("result");
        var logged_user = gs.getUserID();
        var user_detail = new GlideRecord('sys_user');
        user_detail.addQuery('sys_id', logged_user);
        user_detail.query();
        while (user_detail.next()) {
            result.setAttribute("phone", user_detail.mobile_phone);
            result.setAttribute("email", user_detail.email);

        }
    },

    type: 'companyUtil'
});
