sys_script_include.script:
var IMITADHardwareFilterDetailUtils = Class.create();
IMITADHardwareFilterDetailUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	HardwareModuleFilter : function()
	{
		var Data = "";
		var qString = "";
		var sysIDs = [];


		var grproperty = new GlideRecord('x_inmis_im_itad_filter');
		grproperty.addQuery('table','alm_hardware');
		grproperty.query();
		while(grproperty.next())
		{
				qString = grproperty.u_filter;
		}
		if (qString != '')
		{
			var grHardware = new GlideRecord('alm_hardware');
			grHardware.addEncodedQuery(qString);
			grHardware.query();
			while(grHardware.next())
			{
				sysIDs.push(grHardware.getValue('sys_id'));
			}
			return sysIDs;
		}else
			{
				var grL = new GlideRecord('alm_hardware');
				grL.query();
				while(grL.next())
				{
					sysIDs.push(grL.getValue('sys_id'));
				}
			return sysIDs;
			}
	},

	hardwareReferenceQualifier : function()
	{
		var qString = "";

		var grproperty = new GlideRecord('x_inmis_im_itad_filter');
		grproperty.addQuery('table','alm_hardware');
		grproperty.query();
		while(grproperty.next())
		{
				qString = "^"+grproperty.u_filter;
		}

		return qString;
	},

    type: 'IMITADHardwareFilterDetailUtils'
});
