sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    try {
        if (ignore != true && error != true) {
            var IMITADHardware = new GlideRecord('x_inmis_im_itad_imitad_hardware');
            IMITADHardware.addEncodedQuery('workorder=' + source.requestnumber);
            IMITADHardware.query();
            while (IMITADHardware.next()) {
                IMITADHardware.workorder_status = source.orderstatus;
                IMITADHardware.setWorkflow(false);
                IMITADHardware.autoSysFields(false);
                IMITADHardware.update();
            }
        }
    } catch (ex) {
        gs.error('Error from Box or Label Order Update map:' + ex);
    }


})(source, map, log, target);
