sys_ui_action.script:
function checkErasure() {
    var selSysIds = g_list.getChecked();
    var sysIdList = selSysIds.split(',');

    if (!selSysIds) {
        alert('Please select minimum one IMITAD Hardware assets to initiate erasure.');
        return;
    }

    getValueByID(sysIdList);
}



function getValueByID(sys_ids) {

    var ga = new GlideAjax('x_inmis_im_itad.ErasureUtils');
    ga.addParam('sysparm_name', 'checkErasureStates');
    ga.addParam('SysID', sys_ids);
    ga.getXMLAnswer(checkValidations);


    function checkValidations(response) {
        var answer = JSON.parse(response);
      //  alert('Answer : ' + response);
        var flag = answer.flag;
        if (flag.toString() == 'true' || flag == true) {
            alert(answer.msg);
        } else {
            var dialog = new GlideDialogWindow("x_inmis_im_itad_Erasure_Validation"); //Render the dialog containing the UI Page 'task_comments_dialog'
            dialog.setTitle("Please enter scheduling details"); //Set the dialog title
            dialog.setPreference("selected_records", sys_ids);
            dialog.setSize(400, 100);
            dialog.render(); //Open the dialog
        }
    }
}
