sys_ui_action.script:
function readyForErasure() {
    var selSysIds = g_list.getChecked();
    var sysIdList = selSysIds.split(',');

    if (!selSysIds) {
        alert('Please select assets to mark state as ready for Erasure.');
        return;
    }
    getValueByID(sysIdList);
}


function getValueByID(sys_ids) {
    var ga = new GlideAjax('x_inmis_im_itad.ErasureUtils');
    ga.addParam('sysparm_name', 'validateAssetsStatus');
    ga.addParam('SysID', sys_ids);
    ga.getXMLAnswer(checkValidations);


    function checkValidations(response) {
        var answer = JSON.parse(response);
      //  alert('Answer : ' + response);
        var flag = answer.flag;
        if (flag.toString() == 'true' || flag == true) {
            alert(answer.message);
        } else {
            alert('Selected assets have been successfully marked as "Ready for Erasure"');
			location.reload(true);

            if (g_user.hasRole('admin') || g_user.hasRole('x_inmis_im_itad.imitad_admin')) {
                action.setReturnURL(current);
            } else {
                action.setReturnURL(current);
            }

        }
    }
}
