sys_ui_page.client_script:
addLoadEvent( function() {
document.getElementById("sys_script.collection").value = "cmn_location";
document.getElementById("select2-chosen-1").innerHTML = "cmn_location [cmn_location]";
loadFilterColumns('sys_script.filter_condition', 'collection');
	document.getElementById('sys_script.collection').style.align   = 'left';

	var locationFilter = gel('sys_script.collection');
	locationFilter.readOnly = true;

	gel('sys_script.filter_condition').value = getFilter('sys_script.filter_condition');


});


function validateForm() 
{
	var grproperty = new GlideRecord('sys_properties');
	grproperty.addQuery('name','x_inmis_im_itad.ImitadLocationDetails');
	grproperty.query();
	if(grproperty.next())
	{
		grproperty.value = getFilter('sys_script.filter_condition');
		grproperty.update();
	}
}
