sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        var body = request.body.data;
        gs.info('body: ' + JSON.stringify(body));
        var requestNumber = body.InputArguments.OrderUpdate.RequestNumber;
        var SourceID = body.InputArguments.OrderUpdate.SourceID;
        var IMOrderId = body.InputArguments.OrderUpdate.IMOrderId;
        var OrderStatus = body.InputArguments.OrderUpdate.OrderStatus;
        var ShippedDate = body.InputArguments.OrderUpdate.ShippedDate;
        var gdt = ShippedDate ? new GlideDateTime(ShippedDate) :'';
        var TrackingNumber = body.InputArguments.OrderUpdate.TrackingNumber;
        var Tracking = body.InputArguments.Tracking;
        var TrackingNo = Tracking[0].TrackingNo;
        var ReturnTracking = Tracking[0].ReturnTracking;

        var orderUpdate = new GlideRecord('x_inmis_im_itad_box_or_label_order_update');
        orderUpdate.initialize();
        orderUpdate.requestnumber = requestNumber;
        orderUpdate.imorderid = IMOrderId;
        orderUpdate.orderstatus = OrderStatus;
        orderUpdate.sourceid = SourceID;
        orderUpdate.shippeddate = gdt ? gdt.getDate() : '';
        orderUpdate.trackingnumber = TrackingNumber;

        orderUpdate.tracking = TrackingNo;
        orderUpdate.return_tracking = ReturnTracking;
        orderUpdate.insert();



        response.setContentType('application/json');
        var responseBody = {
            "Message": "Updated Box/Label details for :" + requestNumber
        };
        response.setBody(responseBody);
    } catch (ex) {
        gs.error("There was an error doing Order Update API Call", ex);
    }
})(request, response);
