sysauto_script.script:
function callAPIS(domain) {
    // pull Release for Ship Data
    var RelaseForShip = new x_inmis_im_itad.BoxLabelUtils().pullBoxLabelOrderUpdateData(domain, 'Released for Ship');

    // pull picking Data
    var Picking = new x_inmis_im_itad.BoxLabelUtils().pullBoxLabelOrderUpdateData(domain, 'Picking');

    // pull Shipped Data
    var Shipped = new x_inmis_im_itad.BoxLabelUtils().pullBoxLabelOrderUpdateData(domain, 'Shipped');

}

var customerRegistration = new GlideRecord('x_inmis_im_itad_customer_registration');
customerRegistration.query();
while (customerRegistration.next()) {
    if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
        if (!customerRegistration.company.sys_domain.primary) {
            callAPIS(customerRegistration);
        }
    } else {
        callAPIS(customerRegistration);
    }
}
