sysauto_script.script:
function callAPIS(domain) {
    // pull Attachments Data
    var Attachment = new x_inmis_im_itad.WOIntegrationDataLoad().pullWorkOrderAttachmentData(domain);

    // pull AssetLevelAttachment Data
    var AssetLevelAttachment = new x_inmis_im_itad.WOIntegrationDataLoad().pullWorkOrderAssetLevelAttachmentData(domain);
}

var customerRegistration = new GlideRecord('x_inmis_im_itad_customer_registration');
customerRegistration.query();
while (customerRegistration.next()) {
    // gs.info('Domain values: ' + customerRegistration.getValue('sys_domain'));
    if (gs.getProperty("glide.sys.domain.partitioning") == 'true') {
        if (!customerRegistration.company.sys_domain.primary) {
            callAPIS(customerRegistration);
        }
    } else {
        callAPIS(customerRegistration);
    }
}
