// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
try {
    answer = [];
    var approval1 = [];
    var approvalModel = gs.getProperty('x_inmis_im_itad.Approvalmodel');
    if (approvalModel == 'Manager') {
        //         gs.info('test current.hardware_assets.assigned_to.manager: ' + current.hardware_assets.assigned_to.manager + ', agent_id: ' + current.agent_id);
        //         gs.info('test hardware_assets: ' + current.hardware_assets + ', assigned to: ' + current.hardware_assets.assigned_to + ', asset_tag: ' + current.hardware_assets.asset_tag);
        var hardwareAsset = new GlideRecord('alm_hardware');
        if (hardwareAsset.get(current.hardware_assets)) {
            answer.push(hardwareAsset.assigned_to.manager.toString());
            approval1.push(hardwareAsset.assigned_to.manager.toString());
        }
    } else {
       // answer.push('cc0317fedb8460108a521fe968961905'); //ITAD Erasure Level 1
		answer.push(gs.getProperty("x_inmis_im_itad.ITAD Erasure Level 1 Approval group")); //ITAD Erasure Level 1

        var groupMember = new GlideRecord('sys_user_grmember');
       // groupMember.addEncodedQuery('group=cc0317fedb8460108a521fe968961905');
		groupMember.addEncodedQuery('group='+gs.getProperty('x_inmis_im_itad.ITAD Erasure Level 1 Approval group'));
        groupMember.query();
        while (groupMember.next()) {
            approval1.push(groupMember.user.toString());
        }
    }
	
	current.approver1 = approval1.join(',').toString();


} catch (e) {
    gs.info('test >>>Exception occured: ' + e);
}

